import Vue from 'vue'
import Router from 'vue-router'
import Meta from 'vue-meta'

const Home = () => import('@/components/pages/Home')
const About = () => import('@/components/pages/About')
const Contact = () => import('@/components/pages/Contact')

Vue.use(Router)
Vue.use(Meta)

export function createRouter() {
  return new Router({
    linkActiveClass: 'open active',
    mode: 'history',
    scrollBehavior: () => ({ y: 0 }),
    routes: [
      {
        path: '/',
        name: 'home',
        component: Home
      },
      {
        path: '/about',
        name: 'about',
        component: About
      },
      {
        path: '/contact',
        name: 'contact',
        component: Contact
      }
    ]
  })
}

// this is aliased in webpack config based on server/client build
import { createAPI } from 'create-api'

const api = createAPI({
  config: {
    baseURL: 'https://www.reddit.com/'
  }
})

export function fetchApi(request) {
  return api.get(request).then(response => {
    return response.data
  })
}

import axios from 'axios'

export function createAPI({ config }) {
  return axios.create(config)
}

import Vue from 'vue'
import Vuex from 'vuex'
import { fetchApi } from '../api'

Vue.use(Vuex)

export function createStore() {
  return new Vuex.Store({
    state: {
      posts: {/* Массив новостей Reddit */}
    },
    actions: {
      FETCH_POSTS: ({ commit }, subReddit) => {
        const url = 'r/' + subReddit + '/top.json?limit=3&count=3'
        fetchApi(url).then(response => commit('SET_ITEMS', response.data.children))
      }
    },
    mutations: {
      SET_ITEMS: (state, items) => {
        items.forEach((item, i) => {
          if (item) {
            Vue.set(state.posts, i, item.data)
          }
        })
      }
    },
    getters: {
      posts: state => state.posts,
      loading: state => state.loading
    }
  })
}

